import React from 'react'
import Card from '../UI/Card'
import './Logsform.css'


export default function Logsform() {
    
    let inputDate = "";
    let inputDesc = "";
    let inputTime = 0;


    const descChangehandle = (e)=>{
        inputDesc = e.target.value;
    }

    const timeChangehandle = (e)=>{
        inputTime = e.target.value
    }

    const dateChangehandle = (e)=>{
        inputDate = e.target.value
    }

    const submitHandle = (e)=>{
        e.preventDefault();
        const newLogs = {
            date:inputDate,
            desc:inputDesc,
            time:inputTime
        }
    }


  return (
    <Card className="logs-form">
        <form  onSubmit={submitHandle}>
            <div>
                <label htmlFor="date">日期</label>
                <input id='date' type="date" onChange = {dateChangehandle}/>
            </div>

            <div>
                <label htmlFor="desc">內容</label>
                <input id='desc' type="text" onChange = {descChangehandle}/>
            </div>
            
            <div>
                <label htmlFor="time">時間</label>
                <input id='time' type="number" onChange = {timeChangehandle}/>
            </div>

            <div>
                <button>添加</button>
            </div>
        </form>
    </Card>
  )
}
