import React, { useState } from 'react'
import Card from '../UI/Card'
import './Logsform.css'


export default function Logsform() {
    
    const [inputDate,setinputDate] = useState();
    const [inputDesc,setinputDesc] = useState();
    const [inputTime,setinputTime] = useState();


    const descChangehandle = (e)=>{
        setinputDesc(e.target.value)
    }

    const timeChangehandle = (e)=>{
        setinputTime(e.target.value)
    }

    const dateChangehandle = (e)=>{
        setinputDate(e.target.value)
    }

    const submitHandle = (e)=>{
        e.preventDefault();
        const newLogs = {
            date:inputDate,
            desc:inputDesc,
            time:+inputTime,
        }
        

        console.log(newLogs);
        
        setinputDate("");
        setinputDesc("");
        setinputTime("");
    }


  return (
    <Card className="logs-form">
        <form  onSubmit={submitHandle}>
            <div>
                <label htmlFor="date">日期</label>
                <input id='date' value={inputDate} type="date" onChange = {dateChangehandle}/>
            </div>

            <div>
                <label htmlFor="desc">內容</label>
                <input id='desc' value={inputDesc} type="text" onChange = {descChangehandle}/>
            </div>
            
            <div>
                <label htmlFor="time">時間</label>
                <input id='time' value={inputTime} type="number" onChange = {timeChangehandle}/>
            </div>

            <div>
                <button>添加</button>
            </div>
        </form>
    </Card>
  )
}
