import React from 'react'
import Items from './logs-items'
import Card from '../UI/Card'
import './logs.css'
import { useState } from 'react'
import { useRef } from 'react'

export default function Logs(props) {
  
  
  const refCotain = useRef();
  const [counter,setCounter] = useState(1);
  const [user,setUser] = useState({name:"屌男",age:18});
  const tt = ()=>{
    setTimeout(()=>{
      setCounter((preCounter)=>{
        return preCounter + 1
      })
    },1000)
  }


    
  

  const tt2 = ()=>{
   

    setUser({...user,name:"艾倫"})
  }

  const tt3 = ()=>{
    refCotain.current.innerHTML = 8888888888888888
  }

  const dataBase = props.logsData.map(items => <Items onDelete={props.onDelete} key={items.id} date={items.date}  desc={items.desc} time={items.time}/>)

  if(dataBase.length == 0){
     <></>
  }
  return (
    <Card className='logs'>
      {dataBase}
      


      <div className='test' ref={refCotain}>
        {counter} -- {user.name} -- {user.age}
        <button onClick = {tt}>
          安安你好這是測試
        </button></div>

        <button onClick={tt2}>
          安安你好這是測試2
        </button>

        <button onClick={tt3}>
          ref容器
        </button>
      </Card>
    
  )
}
